import React from 'react';

class BookItem extends React.Component {
    render() {
        const { title, imageLinks, authors } = this.props.book.volumeInfo;
        return (
            <div>
                <img className="thumb"
                    alt={title}
                    src={
                        imageLinks === undefined
                            ? ""
                            : `${imageLinks.thumbnail}`
                    }
                />
                <p>{title}</p>
                <p>{authors}</p>
            </div >
        );
    }
}

export default BookItem;
